export default function ProductList() {
  const addToCart = useCartStore((state) => state.addToCart);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [search, setSearch] = useState("");
  const [filter, setFilter] = useState("All");
  const [sort, setSort] = useState("None");

  const categories = ["All", ...new Set(products.map((p) => p.category))];

  
  let filteredProducts = products.filter((p) => {
    const matchCategory = filter === "All" || p.category === filter;
    const matchSearch = p.name.toLowerCase().includes(search.toLowerCase());
    return matchCategory && matchSearch;
  });

  if (sort === "LowToHigh") filteredProducts.sort((a, b) => a.price - b.price);
  else if (sort === "HighToLow") filteredProducts.sort((a, b) => b.price - a.price);

  return (
    <div>
      <div className="filter-bar">
        <input
          type="text"
          placeholder="🔍 Search products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          {categories.map((cat) => (
            <option key={cat}>{cat}</option>
          ))}
        </select>
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="None">Sort by Price</option>
          <option value="LowToHigh">Low → High</option>
          <option value="HighToLow">High → Low</option>
        </select>
      </div>

      <div className="product-container-wrapper">
        <div className="product-container">
          {filteredProducts.map((p) => (
            <div className="product-card" key={p.id}>
              <img
                src={p.image}
                alt={p.name}
                className="product-img"
                onClick={() => setSelectedProduct(p)}
              />
              <h3>{p.name}</h3>
              <p>₹{p.price.toLocaleString()}</p>
              <button className="add-btn" onClick={() => addToCart(p)}>
                🛒 Add to Cart
              </button>
              <button className="view-btn" onClick={() => setSelectedProduct(p)}>
                👁️ View Details
              </button>
            </div>
          ))}
        </div>
      </div>

      {selectedProduct && (
        <div className="modal-overlay" onClick={() => setSelectedProduct(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedProduct.image}
              alt={selectedProduct.name}
              className="modal-img"
            />
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            <h3>₹{selectedProduct.price.toLocaleString()}</h3>
            <div className="modal-buttons">
              <button
                className="add-btn"
                onClick={() => {
                  addToCart(selectedProduct);
                  setSelectedProduct(null);
                }}
              >
                🛒 Add to Cart
              </button>
              <button className="close-btn" onClick={() => setSelectedProduct(null)}>
                ❌ Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
